INSERT INTO `tk_options` VALUES (201, 'option-desc', 'Với mong muốn đem đến cho khách hàng sự an toàn, dịch vụ thẩm mỹ đẳng cấp theo tiêu chuẩn quốc tế và những trải nghiệm hoàn toàn mới, chúng tôi luôn tiên phong trong việc ứng dụng công nghệ mới, cùng họp tác với các chuyên gia hàng đâu quốc tế trong lĩnh vực thẩm mỹ.', 'yes');
