INSERT INTO `tk_posts` VALUES (47, 1, '2019-11-27 09:46:30', '2019-11-27 09:46:30', '<em>Phụ nữ thừa cân hay béo phì luôn mặc cảm về ngoại hình của mình, không chỉ vậy, họ luôn cảm thấy nặng nề trong mọi hoạt động xã hội và thực sự mất tự tin với những bộ đồ bó sát.</em>', 'Giảm béo', 'Phụ nữ thừa cân hay béo phì luôn mặc cảm về ngoại hình của mình, không chỉ vậy, họ luôn cảm thấy nặng nề trong mọi hoạt động xã hội và thực sự mất tự tin với những bộ đồ bó sát.', 'publish', 'open', 'closed', '', 'giam-beo', '', '', '2019-11-27 09:51:13', '2019-11-27 09:51:13', '', 0, 'http://localhost/thuonghieu/?post_type=services&#038;p=47', 0, 'services', '', 0);
